function convertUnits(){const e=parseFloat(document.getElementById("value1").value),t=document.getElementById("unit1").value,n=document.getElementById("unit2").value,o=parseFloat(document.getElementById("rootSize").value);if(isNaN(e)||isNaN(o))return void(document.getElementById("result").textContent="Введите корректные данные.");let c,l;"px"===t?c=e:"rem"!==t&&"em"!==t||(c=e*o),"px"===n?l=c:"rem"!==n&&"em"!==n||(l=c/o);const u=Number.isInteger(l)?l.toString():l.toFixed(4);document.getElementById("value2").value=u,document.getElementById("result").textContent=`${u}${n}`}function copyToClipboard(){const e=document.getElementById("result").textContent.replace("","");navigator.clipboard.writeText(e).then((()=>{const e=document.getElementById("copyButton");e.classList.add("__active"),setTimeout((()=>{e.classList.remove("__active")}),2e3)})).catch((e=>{console.error("Ошибка копирования: ",e)}))}convertUnits();